sys_variable_value.value:
(function paginate(variables, pageResponse) {

    var totalRecs = parseInt(variables.glaciers_per_page) * parseInt(variables.num_pages);
    var lastRec = parseInt(variables.start_with) + totalRecs;

    if (parseInt(variables.glacier_number) < lastRec) {
        variables.getNextPage = true;
    } else {
        variables.getNextPage = false;
    }

})(variables, pageResponse);

